var extname = require('path').extname;

module.exports = function (options) {
	options = options || {};
	var ext = options.ext || 'html',
		regexp = options.regexp || /<!-- more -->/,
		key = options.key || 'less';
		morekey = 'more';

	ext = ext[0] === '.' ? ext : '.' + ext;
	if (typeof regexp === 'string') {
		regexp = new RegExp(regexp);
	}

	return function (files, metalsmith, done) {
		Object.keys(files).forEach(function (file) {
			var contents = files[file].contents,
				index;

			if (extname(file) !== ext || !contents) {
				return;
			}
			index = String.fromCharCode.apply(null, new Uint16Array(contents)).search(regexp)
			length = '<!-- more -->'.length
			files[file][key] = index > -1 ? contents.toString('utf8', 0, index) : contents;
			files[file][morekey] = index > -1 ? contents.toString('utf8', index + length) : '';
		});
		done();
	};
};